import styled from 'styled-components';
import countries from './countries'


const App=() => {

  const [countryID, setCountryID]=useState('');

  const getCountryByClick=(event)=>{
    const theID=event.target.id;
    console.log(theID);
  }

  return (
    <div className="App">
      <header className="App-header">
        <select value="Select Country">
          {countries.map(el=>{
            return <option id={el.alpha2}
                    onClick={getCountryByClick}
                    >{el.name}</option>
          })}
        </select>


      </header>
    </div>
  );
};

export default App;
